import logo from './logo.svg';
import './App.css';
import Button from 'react-bootstrap/Button';
import { CardHeader } from 'react-bootstrap';
import FormLabel from 'react-bootstrap';
import { Component } from 'react';

function App() {

  class App extends Component{

    constructor(props){
      super(props)
      this.state ={
        firstname: ''

      }
    }

    handleFirstName = (event) => {
      this.setState({
        firstname : event.target
      })
    }
  }
  return (
    // code used from W3 schools link: https://www.w3schools.com/bootstrap/bootstrap_get_started.asp
    <div className="App">
  <div style={{color:"black"}} class="jumbotron text-center">
  <h2> Use Sign UP</h2>

  <form>

  <label>First Name</label>
    <input type="text" value={this.state.firstname} onChange={this.handleFirstName}/>


    <label>Last Name</label>
    <input type='text'/>

    <label>Email</label>
    <input type='email'/>

    <label>Password</label>
    <input type='password'/>

    <Button> Submit</Button>


  </form>








      <img src={logo} alt="added basic react logo"  />





  </div>
</div>




  );


}

export default App;
